CREATE TABLE kimia_farma.kf_analisis1 ASSELECT    ft.transaction_id,    ft.date,    kc.branch_id,    kc.branch_name,    kc.kota,    kc.provinsi,    kc.rating as rating_cabang,    ft.customer_name,    ft.product_id,    pd.product_name,    ft.price as actrual_price,    ft.discount_percentage,    CASE        WHEN ft.price <= 50000 THEN 0.1        WHEN ft.price <= 100000 THEN 0.15        WHEN ft.price <= 300000 THEN 0.2        WHEN ft.price <= 500000 THEN 0.25        ELSE 0.3    END AS persentase_gross_laba,    ft.price * (1 - ft.discount_percentage) AS nett_sales,    ft.price * (1 - ft.discount_percentage) *         CASE            WHEN ft.price <= 50000 THEN 0.1            WHEN ft.price <= 100000 THEN 0.15            WHEN ft.price <= 300000 THEN 0.2            WHEN ft.price <= 500000 THEN 0.25            ELSE 0.3        END AS nett_profit,    ft.rating as rating_transaksiFROM    kimia_farma.kf_final_transaction as ftJOIN     kimia_farma.kf_kantor_cabang as kc ON ft.branch_id = kc.branch_idJOIN    kimia_farma.kf_product as pd ON ft.product_id = pd.product_id;